Here's an example of how to implement ball-rectangle collision using Raylib in C++. I'll break it down into steps:

```cpp
class Ball {
public:
    Vector2 position;
    float radius;
    Vector2 speed;

    Ball() {
        position = { (float)GetScreenWidth()/2, (float)GetScreenHeight()/2 };
        radius = 10;
        speed = { 5.0f, 5.0f };  // x and y speed
    }

    void Update() {
        // Move the ball
        position.x += speed.x;
        position.y += speed.y;

        // Bounce off screen edges
        if (position.x >= GetScreenWidth() || position.x <= 0) speed.x *= -1;
        if (position.y >= GetScreenHeight() || position.y <= 0) speed.y *= -1;
    }

    void Draw() {
        DrawCircle(position.x, position.y, radius, WHITE);
    }

    Rectangle GetBoundingBox() {
        return Rectangle{ 
            position.x - radius, 
            position.y - radius, 
            radius * 2, 
            radius * 2 
        };
    }
};

class Paddle {
public:
    Rectangle rect;

    Paddle() {
        rect = { 
            GetScreenWidth()/2.0f,  // x position
            GetScreenHeight() - 50,  // y position
            100,                     // width
            20                       // height
        };
    }

    void Draw() {
        DrawRectangleRec(rect, RED);
    }
};

// In your main game loop:
int main() {
    InitWindow(800, 600, "Ball-Rectangle Collision");
    SetTargetFPS(60);

    Ball ball;
    Paddle paddle;

    while (!WindowShouldClose()) {
        // Update
        ball.Update();

        // Check for collision
        if (CheckCollisionRecs(ball.GetBoundingBox(), paddle.rect)) {
            // Reverse ball direction on collision
            ball.speed.y *= -1;

            // Optional: Adjust ball position to prevent sticking
            if (ball.speed.y > 0) {
                ball.position.y = paddle.rect.y - ball.radius;
            } else {
                ball.position.y = paddle.rect.y + paddle.rect.height + ball.radius;
            }
        }

        // Draw
        BeginDrawing();
            ClearBackground(BLACK);
            ball.Draw();
            paddle.Draw();
        EndDrawing();
    }

    CloseWindow();
    return 0;
}
```

